mapper.select:
select distinct m.* from sys_user_role ur, sys_role_resource rs, sys_menu m where ur.role_id= rs.role_id and rs.resource_id = m.id and rs.resource_type = 1 and m.del_flag != -1
and ur.user_id = #{userId}
order by m.menu_order

select distinct m.* from sys_user_role ur, sys_role_resource rs, sys_menu m where ur.role_id = rs.role_id and rs.resource_id = m.id and rs.resource_type = 2 and m.parent_id = #{menuId} and m.del_flag != -1
and ur.user_id = #{userId}
order by m.menu_order
